<?php

namespace TvShowManagerBundle\Repository;
use TvShowManagerBundle\Entity\Episode;

/**
 * EpisodeRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class EpisodeRepository extends \Doctrine\ORM\EntityRepository
{
    public function listEpisode()
    {
        $em = $this->getDoctrine()->getManager();

        $episode = $em->getRepository('TvShowManagerBundle:Episode')
            ->findAll();

        return $episode;

    }

    public function addEpisode($name, $season, $number, $note, $tvshow)
    {
        $em = $this->getDoctrine()->getManager();

        $episode = new Episode();

        $episode->setName($name);
        $episode->setSeason($season);
        $episode->setNumber($number);
        $episode->setNote($note);
        $episode->setTvSHow($tvshow);

        $em->persist($episode);

        $em->flush();
    }

    public function updateEpisode($id, $name, $season, $number, $note, $tvshow)
    {
        $em = $this->getDoctrine()->getManager();

        $episode= $em->getRepository('TvShowManagerBundle:Episode')->find($id);

        $episode->setName($name);
        $episode->setSeason($season);
        $episode->setNumber($number);
        $episode->setNote($note);
        $episode->setTvSHow($tvshow);

        $em->flush();

    }

    public function deleteEpisode($id)
    {
        $em = $this->getDoctrine()->getManager();

        $episode= $em->getRepository('TvShowManagerBundle:Episode')->find($id);

        $em->remove($episode);

        $em->flush();
    }
}
